function client_testimonials_details_save( $post_id ) {
	if ( defined( 'DOING_AUTOSAVE' ) && DOING_AUTOSAVE ) return;
	if ( ! isset( $_POST['client_testimonials_details_nonce'] ) || ! wp_verify_nonce( $_POST['client_testimonials_details_nonce'], '_client_testimonials_details_nonce' ) ) return;
	if ( ! current_user_can( 'edit_post', $post_id ) ) return;

	if ( isset( $_POST['client_testimonials_details_client_name'] ) )
		update_post_meta( $post_id, 'client_testimonials_details_client_name', esc_attr( $_POST['client_testimonials_details_client_name'] ) );
	if ( isset( $_POST['client_testimonials_details_client_designation'] ) )
		update_post_meta( $post_id, 'client_testimonials_details_client_designation', esc_attr( $_POST['client_testimonials_details_client_designation'] ) );
	if ( isset( $_POST['client_testimonials_details_client_company'] ) )
		update_post_meta( $post_id, 'client_testimonials_details_client_company', esc_attr( $_POST['client_testimonials_details_client_company'] ) );
}
add_action( 'save_post', 'client_testimonials_details_save' );

