function loan_program_icon_save( $post_id ) {
	if ( defined( 'DOING_AUTOSAVE' ) && DOING_AUTOSAVE ) return;
	if ( ! isset( $_POST['loan_program_icon_nonce'] ) || ! wp_verify_nonce( $_POST['loan_program_icon_nonce'], '_loan_program_icon_nonce' ) ) return;
	if ( ! current_user_can( 'edit_post', $post_id ) ) return;

	if ( isset( $_POST['loan_program_icon_font_awesome_tag'] ) )
		update_post_meta( $post_id, 'loan_program_icon_font_awesome_tag', esc_attr( $_POST['loan_program_icon_font_awesome_tag'] ) );
}
add_action( 'save_post', 'loan_program_icon_save' );

